mapper.select:
SELECT substring(event_date,9,2) AS idate ,round(SUM(event_volume)/1024/1024) AS usage_data FROM IS_STATS
WHERE event_date LIKE '2021-04%' AND stats_period ='day'
GROUP BY event_date;

SELECT substring(event_date,9,2) AS idate ,round(SUM(event_volume)/1024/1024) AS usage_data FROM IS_STATS
WHERE event_date LIKE '2021-03%' AND stats_period ='day'
GROUP BY event_date;
